import React, { useState, useEffect, useRef } from 'react';

function NavBar() {
  const [scrolled, setScrolled] = useState(false);
  const [activeSection, setActiveSection] = useState('hero');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showNavbar, setShowNavbar] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [hoveredItem, setHoveredItem] = useState(null);
  const navRef = useRef(null);

  const navItems = [
    { id: 'hero', label: 'Home', icon: 'ðŸ ' },
    { id: 'about', label: 'About', icon: 'ðŸ‘¤' },
    { id: 'projects', label: 'Projects', icon: 'ðŸ’¼' },
    { id: 'skills', label: 'Skills', icon: 'ðŸ› ï¸' },
    { id: 'contact', label: 'Contact', icon: 'ðŸ“§' }
  ];

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      const sections = navItems.map(item => item.id);
      const scrollPosition = currentScrollY + 100;

      // Show/hide navbar based on scroll direction
      if (currentScrollY > lastScrollY && currentScrollY > 100) {
        setShowNavbar(false);
      } else {
        setShowNavbar(true);
      }
      setLastScrollY(currentScrollY);

      // Update active section
      for (const section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const offsetTop = element.offsetTop;
          const offsetHeight = element.offsetHeight;

          if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
            setActiveSection(section);
            break;
          }
        }
      }

      setScrolled(currentScrollY > 50);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  // Close mobile menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (navRef.current && !navRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleClick = (e, id) => {
    e.preventDefault();
    setActiveSection(id);
    setIsMenuOpen(false);

    const element = document.getElementById(id);
    if (element) {
      const offset = 80;
      const bodyRect = document.body.getBoundingClientRect().top;
      const elementRect = element.getBoundingClientRect().top;
      const elementPosition = elementRect - bodyRect;
      const offsetPosition = elementPosition - offset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <>
      {/* Progress Bar */}
      <div 
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: `${Math.min((window.scrollY / (document.documentElement.scrollHeight - window.innerHeight)) * 100, 100)}%`,
          height: '3px',
          background: 'linear-gradient(90deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4)',
          zIndex: 1000,
          transition: 'width 0.1s ease-out'
        }}
      />

      <nav 
        ref={navRef}
        style={{
          backgroundColor: scrolled 
            ? 'rgba(15, 23, 42, 0.95)' 
            : 'linear-gradient(135deg, rgba(15, 23, 42, 0.9), rgba(30, 41, 59, 0.9))',
          background: scrolled 
            ? 'rgba(15, 23, 42, 0.95)' 
            : 'linear-gradient(135deg, #0f172a, #1e293b)',
          color: 'white',
          padding: '0.75rem 1rem',
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 999,
          boxShadow: scrolled 
            ? '0 8px 32px rgba(0, 0, 0, 0.3)' 
            : '0 4px 16px rgba(0, 0, 0, 0.1)',
          transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
          backdropFilter: scrolled ? 'blur(20px)' : 'blur(10px)',
          borderBottom: scrolled ? '1px solid rgba(255, 255, 255, 0.1)' : 'none',
          transform: showNavbar ? 'translateY(0)' : 'translateY(-100%)'
        }}
      >
        <div style={{ 
          maxWidth: '1200px', 
          margin: '0 auto',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          {/* Logo */}
          <div style={{
            fontSize: '1.5rem',
            fontWeight: '700',
            background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text',
            cursor: 'pointer',
            transition: 'transform 0.3s ease',
            transform: hoveredItem === 'logo' ? 'scale(1.05)' : 'scale(1)'
          }}
          onMouseEnter={() => setHoveredItem('logo')}
          onMouseLeave={() => setHoveredItem(null)}
          onClick={(e) => handleClick(e, 'hero')}
          >
            Portfolio
          </div>

          {/* Desktop Navigation */}
          <ul 
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              listStyle: 'none',
              padding: 0,
              margin: 0,
              '@media (max-width: 768px)': {
                display: 'none'
              }
            }}
            className="desktop-nav"
          >
            {navItems.map((item, index) => (
              <li key={item.id} style={{ position: 'relative' }}>
                <a 
                  href={`#${item.id}`}
                  style={{
                    textDecoration: 'none',
                    color: activeSection === item.id ? '#fbbf24' : 'white',
                    fontWeight: activeSection === item.id ? '600' : '500',
                    padding: '0.75rem 1.25rem',
                    borderRadius: '12px',
                    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                    position: 'relative',
                    fontSize: '0.95rem',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem',
                    background: activeSection === item.id 
                      ? 'linear-gradient(135deg, rgba(251, 191, 36, 0.15), rgba(245, 158, 11, 0.15))'
                      : hoveredItem === item.id 
                        ? 'rgba(255, 255, 255, 0.08)'
                        : 'transparent',
                    transform: hoveredItem === item.id ? 'translateY(-2px)' : 'translateY(0)',
                    boxShadow: hoveredItem === item.id 
                      ? '0 8px 25px rgba(0, 0, 0, 0.15)'
                      : 'none'
                  }}
                  onClick={(e) => handleClick(e, item.id)}
                  onMouseEnter={() => setHoveredItem(item.id)}
                  onMouseLeave={() => setHoveredItem(null)}
                >
                  <span style={{ fontSize: '1rem' }}>{item.icon}</span>
                  {item.label}

                  {/* Active indicator */}
                  {activeSection === item.id && (
                    <span 
                      style={{
                        position: 'absolute',
                        bottom: '-8px',
                        left: '50%',
                        transform: 'translateX(-50%)',
                        width: '20px',
                        height: '3px',
                        background: 'linear-gradient(90deg, #fbbf24, #f59e0b)',
                        borderRadius: '2px',
                        animation: 'slideIn 0.3s ease-out'
                      }}
                    />
                  )}
                </a>
              </li>
            ))}
          </ul>

          {/* Mobile Menu Button */}
          <button
            onClick={toggleMenu}
            style={{
              display: 'none',
              background: 'none',
              border: 'none',
              color: 'white',
              fontSize: '1.5rem',
              cursor: 'pointer',
              padding: '0.5rem',
              borderRadius: '8px',
              transition: 'all 0.3s ease',
              transform: isMenuOpen ? 'rotate(90deg)' : 'rotate(0deg)'
            }}
            className="mobile-menu-btn"
          >
            {isMenuOpen ? 'âœ•' : 'â˜°'}
          </button>
        </div>

        {/* Mobile Navigation */}
        <div
          style={{
            display: 'none',
            position: 'absolute',
            top: '100%',
            left: 0,
            right: 0,
            background: 'linear-gradient(135deg, rgba(15, 23, 42, 0.98), rgba(30, 41, 59, 0.98))',
            backdropFilter: 'blur(20px)',
            borderTop: '1px solid rgba(255, 255, 255, 0.1)',
            transform: isMenuOpen ? 'translateY(0)' : 'translateY(-100%)',
            opacity: isMenuOpen ? 1 : 0,
            visibility: isMenuOpen ? 'visible' : 'hidden',
            transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}
          className="mobile-nav"
        >
          <ul style={{
            listStyle: 'none',
            padding: '1rem 0',
            margin: 0,
            maxWidth: '1200px',
            marginLeft: 'auto',
            marginRight: 'auto'
          }}>
            {navItems.map((item, index) => (
              <li key={item.id} style={{
                opacity: isMenuOpen ? 1 : 0,
                transform: isMenuOpen ? 'translateX(0)' : 'translateX(-20px)',
                transition: `all 0.4s cubic-bezier(0.4, 0, 0.2, 1) ${index * 0.1}s`
              }}>
                <a
                  href={`#${item.id}`}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '1rem',
                    padding: '1rem 2rem',
                    textDecoration: 'none',
                    color: activeSection === item.id ? '#fbbf24' : 'white',
                    fontWeight: activeSection === item.id ? '600' : '500',
                    fontSize: '1.1rem',
                    transition: 'all 0.3s ease',
                    borderLeft: activeSection === item.id ? '4px solid #fbbf24' : '4px solid transparent',
                    background: activeSection === item.id 
                      ? 'rgba(251, 191, 36, 0.1)' 
                      : 'transparent'
                  }}
                  onClick={(e) => handleClick(e, item.id)}
                >
                  <span style={{ fontSize: '1.2rem' }}>{item.icon}</span>
                  {item.label}
                </a>
              </li>
            ))}
          </ul>
        </div>
      </nav>

      {/* Demo Sections */}
      <div style={{ paddingTop: '80px' }}>
        {navItems.map((item) => (
          <section
            key={item.id}
            id={item.id}
            style={{
              minHeight: '100vh',
              padding: '4rem 2rem',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              background: item.id === 'hero' 
                ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
                : item.id === 'about'
                ? 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)'
                : item.id === 'projects'
                ? 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'
                : item.id === 'skills'
                ? 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)'
                : 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',
              color: 'white'
            }}
          >
            <div style={{ textAlign: 'center', maxWidth: '600px' }}>
              <h1 style={{ 
                fontSize: '3rem', 
                marginBottom: '1rem',
                textShadow: '2px 2px 4px rgba(0,0,0,0.3)'
              }}>
                {item.icon} {item.label}
              </h1>
              <p style={{ 
                fontSize: '1.2rem', 
                opacity: 0.9,
                lineHeight: 1.6
              }}>
                This is the {item.label.toLowerCase()} section. Lorem ipsum dolor sit amet, 
                consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et 
                dolore magna aliqua.
              </p>
            </div>
          </section>
        ))}
      </div>

      <style jsx>{`
        @media (max-width: 768px) {
          .desktop-nav {
            display: none !important;
          }
          .mobile-menu-btn {
            display: block !important;
          }
          .mobile-nav {
            display: block !important;
          }
        }

        @keyframes slideIn {
          from {
            width: 0;
            opacity: 0;
          }
          to {
            width: 20px;
            opacity: 1;
          }
        }

        /* Smooth scrolling */
        html {
          scroll-behavior: smooth;
        }

        /* Custom scrollbar */
        ::-webkit-scrollbar {
          width: 8px;
        }

        ::-webkit-scrollbar-track {
          background: #f1f1f1;
        }

        ::-webkit-scrollbar-thumb {
          background: linear-gradient(135deg, #667eea, #764ba2);
          border-radius: 4px;
        }

        ::-webkit-scrollbar-thumb:hover {
          background: linear-gradient(135deg, #5a67d8, #6b46c1);
        }
      `}</style>
    </>
  );
}

export default NavBar;
