import React from 'react';
import MyImage from '../src/Image/yelow.png';

function Hero() {
    return (
        // ... rest of your component
        <img 
          src={MyImage} // Use the imported image
          alt="Ishan Wijesinghe"
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover'
          }}
        />
        // ... rest of your component
      );

  return (
    <div 
      id="hero"
      style={{
        textAlign: 'center',
        padding: '2rem 1rem',
        backgroundColor: '#f8fafc',
        backgroundImage: 'linear-gradient(to bottom, rgba(255,255,255,0.9), rgba(241,245,249,0.9))',
        minHeight: 'calc(100vh - 60px)',
        display: 'flex',
        flexDirection: 'row', // Changed from column to row
        alignItems: 'center',
        justifyContent: 'space-between', // This will space out the content and image
        position: 'relative',
        overflow: 'hidden',
        maxWidth: '1200px', // Added max-width for better layout control
        margin: '0 auto', // Center the hero section
        padding: '2rem 5%' // Added horizontal padding
      }}
    >
      {/* Animated background elements */}
      <div 
        style={{
          position: 'absolute',
          top: '-50px',
          right: '-50px',
          width: '200px',
          height: '200px',
          borderRadius: '50%',
          backgroundColor: 'rgba(59, 130, 246, 0.1)',
          filter: 'blur(40px)',
          animation: 'float 8s ease-in-out infinite'
        }}
      />
      <div 
        style={{
          position: 'absolute',
          bottom: '20%',
          left: '-50px',
          width: '300px',
          height: '300px',
          borderRadius: '50%',
          backgroundColor: 'rgba(99, 102, 241, 0.1)',
          filter: 'blur(50px)',
          animation: 'float 10s ease-in-out infinite 2s'
        }}
      />

      <div 
        style={{
          maxWidth: '600px', // Adjusted max-width
          margin: '0',
          position: 'relative',
          zIndex: 1,
          textAlign: 'left' // Align text to left
        }}
      >
        <h1 
          style={{
            fontSize: '3.5rem',
            fontWeight: '800',
            color: '#1e293b',
            marginBottom: '1.5rem',
            lineHeight: '1.2',
            letterSpacing: '-0.025em'
          }}
        >
          Hi, I'm <span style={{ color: '#3b82f6' }}>Ishan</span> ðŸ‘‹
        </h1>

        <p 
          style={{
            fontSize: '1.25rem',
            color: '#475569',
            marginBottom: '2.5rem',
            maxWidth: '600px',
            lineHeight: '1.6'
          }}
        >
          Aspiring Full Stack Developer & IT Undergraduate passionate about building modern web applications
        </p>

        <div 
          style={{
            display: 'flex',
            gap: '1rem',
            justifyContent: 'flex-start', // Align buttons to left
            flexWrap: 'wrap'
          }}
        >
          <a 
            href="#projects" 
            style={{
              backgroundColor: '#3b82f6',
              color: 'white',
              padding: '0.75rem 2rem',
              borderRadius: '0.5rem',
              textDecoration: 'none',
              fontWeight: '600',
              fontSize: '1rem',
              transition: 'all 0.2s ease',
              boxShadow: '0 4px 6px rgba(59, 130, 246, 0.3)',
              display: 'inline-block',
              border: '2px solid transparent'
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#2563eb';
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 6px 8px rgba(59, 130, 246, 0.4)';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = '#3b82f6';
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 6px rgba(59, 130, 246, 0.3)';
            }}
          >
            View Projects
          </a>

          <a 
            href="#contact" 
            style={{
              backgroundColor: 'transparent',
              color: '#3b82f6',
              padding: '0.75rem 2rem',
              borderRadius: '0.5rem',
              textDecoration: 'none',
              fontWeight: '600',
              fontSize: '1rem',
              transition: 'all 0.2s ease',
              border: '2px solid #3b82f6',
              display: 'inline-block'
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#f0f7ff';
              e.target.style.transform = 'translateY(-2px)';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.transform = 'translateY(0)';
            }}
          >
            Contact Me
          </a>
        </div>

        <div 
          style={{
            marginTop: '3rem',
            display: 'flex',
            justifyContent: 'flex-start', // Align social icons to left
            gap: '1.5rem'
          }}
        >
          <a 
            href="https://github.com/Dilhara2002" 
            target="_blank" 
            rel="noopener noreferrer"
            style={{
              color: '#64748b',
              transition: 'all 0.2s ease'
            }}
            onMouseEnter={(e) => e.target.style.color = '#3b82f6'}
            onMouseLeave={(e) => e.target.style.color = '#64748b'}
          >
            <svg 
              xmlns="http://www.w3.org/2000/svg" 
              width="24" 
              height="24" 
              viewBox="0 0 24 24" 
              fill="none" 
              stroke="currentColor" 
              strokeWidth="2" 
              strokeLinecap="round" 
              strokeLinejoin="round"
            >
              <path d="M9 19c-5 1.5-5-2.5-7-3m14 6v-3.87a3.37 3.37 0 0 0-.94-2.61c3.14-.35 6.44-1.54 6.44-7A5.44 5.44 0 0 0 20 4.77 5.07 5.07 0 0 0 19.91 1S18.73.65 16 2.48a13.38 13.38 0 0 0-7 0C6.27.65 5.09 1 5.09 1A5.07 5.07 0 0 0 5 4.77a5.44 5.44 0 0 0-1.5 3.78c0 5.42 3.3 6.61 6.44 7A3.37 3.37 0 0 0 9 18.13V22"></path>
            </svg>
          </a>

          <a 
            href="https://www.linkedin.com/in/ishan-wijesinghe-5200a1318/" 
            target="_blank" 
            rel="noopener noreferrer"
            style={{
              color: '#64748b',
              transition: 'all 0.2s ease'
            }}
            onMouseEnter={(e) => e.target.style.color = '#3b82f6'}
            onMouseLeave={(e) => e.target.style.color = '#64748b'}
          >
            <svg 
              xmlns="http://www.w3.org/2000/svg" 
              width="24" 
              height="24" 
              viewBox="0 0 24 24" 
              fill="none" 
              stroke="currentColor" 
              strokeWidth="2" 
              strokeLinecap="round" 
              strokeLinejoin="round"
            >
              <path d="M16 8a6 6 0 0 1 6 6v7h-4v-7a2 2 0 0 0-2-2 2 2 0 0 0-2 2v7h-4v-7a6 6 0 0 1 6-6z"></path>
              <rect x="2" y="9" width="4" height="12"></rect>
              <circle cx="4" cy="4" r="2"></circle>
            </svg>
          </a>

          <a 
            href="mailto:your.email@example.com" 
            style={{
              color: '#64748b',
              transition: 'all 0.2s ease'
            }}
            onMouseEnter={(e) => e.target.style.color = '#3b82f6'}
            onMouseLeave={(e) => e.target.style.color = '#64748b'}
          >
            <svg 
              xmlns="http://www.w3.org/2000/svg" 
              width="24" 
              height="24" 
              viewBox="0 0 24 24" 
              fill="none" 
              stroke="currentColor" 
              strokeWidth="2" 
              strokeLinecap="round" 
              strokeLinejoin="round"
            >
              <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path>
              <polyline points="22,6 12,13 2,6"></polyline>
            </svg>
          </a>
        </div>
      </div>

      {/* Image container - Add your image here */}
      <div 
        style={{
          position: 'relative',
          zIndex: 1,
          width: '400px',
          height: '400px',
          borderRadius: '20px',
          overflow: 'hidden',
          boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)',
          border: '4px solid white',
          marginLeft: '2rem'
        }}
      >
        {/* Replace with your actual image */}
        <img 
          src="" // Replace with your image path
          alt="Ishan Wijesinghe"
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover'
          }}
        />
      </div>

      {/* Animation styles */}
      <style>
        {`
          @keyframes float {
            0% { transform: translateY(0px); }
            50% { transform: translateY(-20px); }
            100% { transform: translateY(0px); }
          }
        `}
      </style>
    </div>
  );
}

export default Hero;
