import React from 'react';

function About() {
  return (
    <div 
      id="about"
      style={{
        padding: '5rem 1rem',
        maxWidth: '1200px',
        margin: '0 auto',
        backgroundColor: '#ffffff',
        position: 'relative'
      }}
    >
      <div 
        style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: '3rem'
        }}
      >
        {/* Section Header */}
        <div 
          style={{
            textAlign: 'center',
            marginBottom: '1rem'
          }}
        >
          <h2 
            style={{
              fontSize: '2.5rem',
              fontWeight: '800',
              color: '#1e293b',
              marginBottom: '1rem',
              position: 'relative',
              display: 'inline-block'
            }}
          >
            About Me
            <span 
              style={{
                position: 'absolute',
                bottom: '-8px',
                left: '0',
                width: '100%',
                height: '4px',
                backgroundColor: '#3b82f6',
                borderRadius: '2px'
              }}
            />
          </h2>
          <p 
            style={{
              fontSize: '1.125rem',
              color: '#64748b',
              maxWidth: '600px'
            }}
          >
            Get to know more about my journey, skills, and passions
          </p>
        </div>

        {/* Content Container */}
        <div 
          style={{
            display: 'flex',
            flexDirection: { xs: 'column', md: 'row' },
            gap: '3rem',
            alignItems: 'center',
            width: '100%'
          }}
        >
          {/* Image/Graphic Placeholder */}
          <div 
            style={{
              flex: '1',
              minWidth: '300px',
              height: '350px',
              borderRadius: '12px',
              backgroundColor: '#f1f5f9',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              overflow: 'hidden',
              boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)'
            }}
          >
            <div 
              style={{
                width: '100%',
                height: '100%',
                background: 'linear-gradient(135deg, #e2e8f0 0%, #f1f5f9 100%)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
            >
              <svg 
                xmlns="http://www.w3.org/2000/svg" 
                width="80" 
                height="80" 
                viewBox="0 0 24 24" 
                fill="none" 
                stroke="#94a3b8" 
                strokeWidth="2" 
                strokeLinecap="round" 
                strokeLinejoin="round"
              >
                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                <circle cx="12" cy="7" r="4"></circle>
              </svg>
            </div>
          </div>

          {/* Text Content */}
          <div 
            style={{
              flex: '1',
              display: 'flex',
              flexDirection: 'column',
              gap: '1.5rem'
            }}
          >
            <div>
              <h3 
                style={{
                  fontSize: '1.5rem',
                  fontWeight: '700',
                  color: '#1e293b',
                  marginBottom: '0.5rem'
                }}
              >
                My Journey
              </h3>
              <p 
                style={{
                  fontSize: '1rem',
                  color: '#475569',
                  lineHeight: '1.7'
                }}
              >
                I'm currently studying Information Technology at the University of Colombo School of Computing. 
                My passion for technology started when I built my first website at 16, and since then I've been 
                obsessed with creating digital solutions that solve real-world problems.
              </p>
            </div>

            <div>
              <h3 
                style={{
                  fontSize: '1.5rem',
                  fontWeight: '700',
                  color: '#1e293b',
                  marginBottom: '0.5rem'
                }}
              >
                My Skills
              </h3>
              <div 
                style={{
                  display: 'flex',
                  flexWrap: 'wrap',
                  gap: '0.75rem',
                  marginBottom: '1rem'
                }}
              >
                {['JavaScript', 'React', 'Node.js', 'Python', 'MongoDB', 'Express', 'Git', 'Docker'].map((skill) => (
                  <span 
                    key={skill}
                    style={{
                      backgroundColor: '#e0f2fe',
                      color: '#0369a1',
                      padding: '0.375rem 0.75rem',
                      borderRadius: '9999px',
                      fontSize: '0.875rem',
                      fontWeight: '500'
                    }}
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>

            <div>
              <h3 
                style={{
                  fontSize: '1.5rem',
                  fontWeight: '700',
                  color: '#1e293b',
                  marginBottom: '0.5rem'
                }}
              >
                My Approach
              </h3>
              <p 
                style={{
                  fontSize: '1rem',
                  color: '#475569',
                  lineHeight: '1.7'
                }}
              >
                I believe in clean, efficient code and user-centric design. When I'm not coding, 
                I'm either contributing to open-source projects, learning new frameworks, or mentoring 
                junior developers in my university's coding club.
              </p>
            </div>
          </div>
        </div>

        {/* Stats/Call to Action */}
        <div 
          style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '2rem',
            width: '100%',
            marginTop: '2rem',
            flexWrap: 'wrap'
          }}
        >
          <div 
            style={{
              textAlign: 'center',
              padding: '1.5rem',
              borderRadius: '8px',
              backgroundColor: '#f8fafc',
              minWidth: '150px',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
            }}
          >
            <div 
              style={{
                fontSize: '2.25rem',
                fontWeight: '700',
                color: '#3b82f6',
                marginBottom: '0.5rem'
              }}
            >
              15+
            </div>
            <div 
              style={{
                fontSize: '0.875rem',
                color: '#64748b',
                fontWeight: '500'
              }}
            >
              Projects Completed
            </div>
          </div>

          <div 
            style={{
              textAlign: 'center',
              padding: '1.5rem',
              borderRadius: '8px',
              backgroundColor: '#f8fafc',
              minWidth: '150px',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
            }}
          >
            <div 
              style={{
                fontSize: '2.25rem',
                fontWeight: '700',
                color: '#3b82f6',
                marginBottom: '0.5rem'
              }}
            >
              2+
            </div>
            <div 
              style={{
                fontSize: '0.875rem',
                color: '#64748b',
                fontWeight: '500'
              }}
            >
              Years of Experience
            </div>
          </div>

          <div 
            style={{
              textAlign: 'center',
              padding: '1.5rem',
              borderRadius: '8px',
              backgroundColor: '#f8fafc',
              minWidth: '150px',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
            }}
          >
            <div 
              style={{
                fontSize: '2.25rem',
                fontWeight: '700',
                color: '#3b82f6',
                marginBottom: '0.5rem'
              }}
            >
              10+
            </div>
            <div 
              style={{
                fontSize: '0.875rem',
                color: '#64748b',
                fontWeight: '500'
              }}
            >
              Technologies Learned
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default About;
