import React from 'react';
import profileImage from '../Image/yelow.png';

const Hero = () => {
  return (
    <section id="hero" style={heroStyles}>
      {/* Content container */}
      <div style={contentContainer}>
        <div style={textContent}>
          <p style={introText}>I'm Ishan,</p>
          <h1 style={titleStyle}>Full Stack Developer</h1>

          <div style={highlightBox}>
            <p style={highlightText}>Modern web applications</p>
            <p style={highlightText}>our website's success</p>
            <p style={highlightText}>Recommended</p>
          </div>

          <div style={buttonContainer}>
            <a href="#projects" style={primaryButton}>
              Portfolio
            </a>
            <a href="#contact" style={secondaryButton}>
              Hire me
            </a>
          </div>

          <div style={experienceBadge}>
            <p style={experienceYears}>2+ Years</p>
            <p style={experienceText}>Experience</p>
          </div>
        </div>

        {/* Profile image with blue overlay */}
        <div style={imageContainer}>
          <div style={blueOverlay} />
          <img src={profileImage} alt="Ishan Wijesinghe" style={imageStyle} />
        </div>
      </div>

      {/* Social links */}
      <div style={socialLinks}>
        <SocialLink href="https://github.com/Dilhara2002">
          <GitHubIcon />
        </SocialLink>
        <SocialLink href="https://www.linkedin.com/in/ishan-wijesinghe-5200a1318/">
          <LinkedInIcon />
        </SocialLink>
        <SocialLink href="mailto:your.email@example.com">
          <EmailIcon />
        </SocialLink>
      </div>
    </section>
  );
};

// Reusable Components
const SocialLink = ({ href, children, ...props }) => (
  <a href={href} target="_blank" rel="noopener noreferrer" style={socialLink} {...props}>
    {children}
  </a>
);

// SVG Components
const GitHubIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M9 19c-5 1.5-5-2.5-7-3m14 6v-3.87a3.37 3.37 0 0 0-.94-2.61c3.14-.35 6.44-1.54 6.44-7A5.44 5.44 0 0 0 20 4.77 5.07 5.07 0 0 0 19.91 1S18.73.65 16 2.48a13.38 13.38 0 0 0-7 0C6.27.65 5.09 1 5.09 1A5.07 5.07 0 0 0 5 4.77a5.44 5.44 0 0 0-1.5 3.78c0 5.42 3.3 6.61 6.44 7A3.37 3.37 0 0 0 9 18.13V22"></path>
  </svg>
);

const LinkedInIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M16 8a6 6 0 0 1 6 6v7h-4v-7a2 2 0 0 0-2-2 2 2 0 0 0-2 2v7h-4v-7a6 6 0 0 1 6-6z"></path>
    <rect x="2" y="9" width="4" height="12"></rect>
    <circle cx="4" cy="4" r="2"></circle>
  </svg>
);

const EmailIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path>
    <polyline points="22,6 12,13 2,6"></polyline>
  </svg>
);

// Styles
const heroStyles = {
  padding: '4rem 5%',
  backgroundColor: '#f0f8ff',
  minHeight: '100vh',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  position: 'relative',
  overflow: 'hidden',
  maxWidth: '1200px',
  margin: '0 auto'
};

const contentContainer = {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  gap: '2rem',
  marginBottom: '3rem'
};

const textContent = {
  maxWidth: '600px',
  position: 'relative',
  zIndex: 1
};

const introText = {
  fontSize: '1.5rem',
  fontWeight: '600',
  color: '#1e3a8a',
  marginBottom: '0.5rem'
};

const titleStyle = {
  fontSize: '3.5rem',
  fontWeight: '800',
  color: '#1e3a8a',
  marginBottom: '1.5rem',
  lineHeight: '1.2'
};

const highlightBox = {
  backgroundColor: '#3b82f6',
  color: 'white',
  padding: '1.5rem',
  borderRadius: '0.5rem',
  marginBottom: '2rem',
  boxShadow: '0 4px 6px rgba(59, 130, 246, 0.3)'
};

const highlightText = {
  fontSize: '1.25rem',
  margin: '0.5rem 0',
  fontWeight: '500'
};

const buttonContainer = {
  display: 'flex',
  gap: '1rem',
  marginBottom: '2rem'
};

const baseButton = {
  padding: '0.75rem 2rem',
  borderRadius: '0.5rem',
  textDecoration: 'none',
  fontWeight: '600',
  fontSize: '1rem',
  transition: 'all 0.2s ease',
  display: 'inline-block'
};

const primaryButton = {
  ...baseButton,
  backgroundColor: '#1e3a8a',
  color: 'white',
  ':hover': {
    backgroundColor: '#1e40af',
    transform: 'translateY(-2px)'
  }
};

const secondaryButton = {
  ...baseButton,
  backgroundColor: 'white',
  color: '#1e3a8a',
  border: '2px solid #1e3a8a',
  ':hover': {
    backgroundColor: '#f0f7ff',
    transform: 'translateY(-2px)'
  }
};

const experienceBadge = {
  display: 'inline-flex',
  flexDirection: 'column',
  alignItems: 'center',
  backgroundColor: '#3b82f6',
  color: 'white',
  padding: '1rem 1.5rem',
  borderRadius: '0.5rem'
};

const experienceYears = {
  fontSize: '1.5rem',
  fontWeight: '700',
  margin: 0
};

const experienceText = {
  fontSize: '1rem',
  margin: 0
};

const imageContainer = {
  position: 'relative',
  width: '400px',
  height: '400px',
  borderRadius: '20px',
  overflow: 'hidden',
  boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)'
};

const blueOverlay = {
  position: 'absolute',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(59, 130, 246, 0.3)',
  zIndex: 1
};

const imageStyle = {
  width: '100%',
  height: '100%',
  objectFit: 'cover',
  position: 'relative'
};

const socialLinks = {
  display: 'flex',
  justifyContent: 'center',
  gap: '1.5rem',
  marginTop: '2rem'
};

const socialLink = {
  color: '#1e3a8a',
  transition: 'all 0.2s ease',
  ':hover': {
    color: '#3b82f6'
  }
};

export default Hero;
