import React from 'react';



function Hero() {
  return React.createElement(
    'div',
    { className: 'text-center py-20 bg-gray-100' },
    React.createElement('h1', { className: 'text-4xl font-bold' }, "Hi, I'm Ishan ðŸ‘‹"),
    React.createElement('p', { className: 'mt-4 text-lg text-gray-700' }, 'Aspiring Full Stack Developer & IT Undergraduate'),
    React.createElement(
      'a',
      { href: '#projects', className: 'mt-6 inline-block bg-blue-600 text-white px-6 py-2 rounded' },
      'View Projects'
    )
  );
}

export default Hero;
