import React, { useState, useEffect, useRef } from 'react';

function Projects() {
  const [filter, setFilter] = useState('all');
  const [isLoading, setIsLoading] = useState(true);
  const [hoveredCard, setHoveredCard] = useState(null);
  const projectRefs = useRef([]);

  const projects = [
    // ... (keep your existing projects array)
  ];

  const categories = [
    // ... (keep your existing categories array)
  ];

  const filteredProjects = filter === 'all' 
    ? projects 
    : projects.filter(project => project.category === filter);

  // Intersection Observer for scroll animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.style.opacity = '1';
            entry.target.style.transform = 'translateY(0px)';
          }
        });
      },
      { threshold: 0.1, rootMargin: '0px 0px -50px 0px' }
    );

    projectRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => observer.disconnect();
  }, [filteredProjects]);

  // Loading animation
  useEffect(() => {
    setIsLoading(true);
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 300);
    return () => clearTimeout(timer);
  }, [filter]);

  // Optimized ParticleBackground component
  const ParticleBackground = React.memo(() => {
    const canvasRef = useRef(null);
    const animationFrameId = useRef(null);
    const particles = useRef([]);

    const initParticles = () => {
      const particleCount = 50;
      particles.current = Array.from({ length: particleCount }, () => ({
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        vx: (Math.random() - 0.5) * 0.5,
        vy: (Math.random() - 0.5) * 0.5,
        size: Math.random() * 2 + 1,
        opacity: Math.random() * 0.5 + 0.1
      }));
    };

    const animate = () => {
      const canvas = canvasRef.current;
      if (!canvas) return;

      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.current.forEach(particle => {
        particle.x += particle.vx;
        particle.y += particle.vy;

        if (particle.x < 0 || particle.x > canvas.width) particle.vx *= -1;
        if (particle.y < 0 || particle.y > canvas.height) particle.vy *= -1;

        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(59, 130, 246, ${particle.opacity})`;
        ctx.fill();
      });

      animationFrameId.current = requestAnimationFrame(animate);
    };

    useEffect(() => {
      const canvas = canvasRef.current;
      if (!canvas) return;

      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      initParticles();

      animationFrameId.current = requestAnimationFrame(animate);

      const handleResize = () => {
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;
      };

      window.addEventListener('resize', handleResize);

      return () => {
        if (animationFrameId.current) {
          cancelAnimationFrame(animationFrameId.current);
        }
        window.removeEventListener('resize', handleResize);
      };
    }, []);

    return (
      <canvas
        ref={canvasRef}
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          zIndex: -1,
          opacity: 0.3
        }}
      />
    );
  });

  const ProjectCard = ({ project, index }) => {
    const [imageLoaded, setImageLoaded] = useState(false);
    const [isFlipped, setIsFlipped] = useState(false);
    const imgRef = useRef(null);

    // Preload image
    useEffect(() => {
      const img = new Image();
      img.src = project.image;
      img.onload = () => {
        if (imgRef.current) {
          setImageLoaded(true);
        }
      };
    }, [project.image]);

    return (
      <div
        ref={el => projectRefs.current[index] = el}
        style={{
          opacity: 0,
          transform: 'translateY(30px)',
          transition: 'all 0.6s ease',
          transitionDelay: `${index * 0.1}s`
        }}
        onMouseEnter={() => setHoveredCard(index)}
        onMouseLeave={() => setHoveredCard(null)}
      >
        {/* ... (keep your existing ProjectCard JSX, but update the image part) */}
        <img
          ref={imgRef}
          src={project.image}
          alt={project.title}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            transition: 'transform 0.3s ease',
            transform: hoveredCard === index ? 'scale(1.1)' : 'scale(1)',
            display: imageLoaded ? 'block' : 'none'
          }}
        />
        {/* ... (rest of your ProjectCard JSX) */}
      </div>
    );
  };

  return (
    <>
      <style>
        {`
          /* ... (keep your existing styles) */
        `}
      </style>

      <ParticleBackground />

      <div
        id="projects"
        style={{
          padding: '5rem 1rem',
          maxWidth: '1400px',
          margin: '0 auto',
          backgroundColor: 'transparent',
          position: 'relative'
        }}
      >
        {/* ... (keep the rest of your JSX exactly as is) */}
      </div>
    </>
  );
}

export default Projects;
