import React, { useState } from 'react';

function NavBar() {
  const [activeLink, setActiveLink] = useState('Home');

  const navStyle = {
    background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
    color: 'white',
    padding: '20px 32px',
    position: 'sticky',
    top: 0,
    zIndex: 50,
    boxShadow: '0 8px 32px rgba(59, 130, 246, 0.3)',
    backdropFilter: 'blur(10px)'
  };

  const ulStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    listStyle: 'none',
    margin: 0,
    padding: 0,
    gap: '8px'
  };

  const baseLinkStyle = {
    color: 'white',
    textDecoration: 'none',
    fontSize: '16px',
    fontWeight: '500',
    padding: '12px 24px',
    borderRadius: '25px',
    transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
    position: 'relative',
    overflow: 'hidden'
  };

  const inactiveLinkStyle = {
    ...baseLinkStyle,
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    border: '1px solid rgba(255, 255, 255, 0.2)'
  };

  const activeLinkStyle = {
    ...baseLinkStyle,
    backgroundColor: 'white',
    color: '#1d4ed8',
    fontWeight: '600',
    transform: 'scale(1.05)',
    boxShadow: '0 4px 20px rgba(255, 255, 255, 0.3)'
  };

  const hoverStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    transform: 'translateY(-2px) scale(1.02)',
    boxShadow: '0 6px 25px rgba(255, 255, 255, 0.2)'
  };

  const handleMouseEnter = (e) => {
    if (activeLink !== e.target.textContent) {
      Object.assign(e.target.style, { ...inactiveLinkStyle, ...hoverStyle });
    }
  };

  const handleMouseLeave = (e) => {
    if (activeLink !== e.target.textContent) {
      Object.assign(e.target.style, inactiveLinkStyle);
    }
  };

  const handleClick = (section, e) => {
    e.preventDefault();
    setActiveLink(section);
    const element = document.getElementById(section.toLowerCase());
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const getLinkStyle = (section) => activeLink === section ? activeLinkStyle : inactiveLinkStyle;

  return (
    <nav style={navStyle}>
      <ul style={ulStyle}>
        {['Home', 'About', 'Projects', 'Contact'].map((label) => (
          <li key={label}>
            <a
              href={`#${label.toLowerCase()}`}
              style={getLinkStyle(label)}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
              onClick={(e) => handleClick(label, e)}
            >
              {label}
            </a>
          </li>
        ))}
      </ul>
    </nav>
  );
}

export default NavBar;
