import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';

// NavBar Component
function NavBar() {
  const [activeLink, setActiveLink] = useState('Home');
  const navLinks = ['Home', 'About', 'Projects', 'Contact'];

  const handleClick = (label, e) => {
    e.preventDefault();
    setActiveLink(label);
    const section = document.getElementById(label.toLowerCase());
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const getLinkStyle = (label) => ({
    color: activeLink === label ? '#1d4ed8' : 'white',
    backgroundColor: activeLink === label ? 'white' : 'rgba(255,255,255,0.1)',
    border: activeLink === label ? 'none' : '1px solid rgba(255,255,255,0.2)',
    fontWeight: activeLink === label ? '600' : '500',
    padding: '12px 24px',
    borderRadius: '25px',
    transition: 'all 0.3s ease',
    textDecoration: 'none',
    fontSize: '16px',
    display: 'inline-block',
    boxShadow: activeLink === label ? '0 4px 20px rgba(255, 255, 255, 0.3)' : 'none',
  });

  const navStyle = {
    background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
    padding: '20px 32px',
    position: 'sticky',
    top: 0,
    zIndex: 50,
    boxShadow: '0 8px 32px rgba(59, 130, 246, 0.3)',
    color: 'white'
  };

  return (
    <nav style={navStyle}>
      <ul style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        listStyle: 'none',
        gap: '10px',
        margin: 0,
        padding: 0
      }}>
        {navLinks.map((label) => (
          <li key={label}>
            <a
              href={`#${label.toLowerCase()}`}
              onClick={(e) => handleClick(label, e)}
              style={getLinkStyle(label)}
            >
              {label}
            </a>
          </li>
        ))}
      </ul>
    </nav>
  );
}

// Section Component
function Section({ id, title, bgColor }) {
  return (
    <section
      id={id}
      style={{
        height: '100vh',
        padding: '80px 20px',
        backgroundColor: bgColor,
        color: '#333',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '2rem'
      }}
    >
      {title} Section
    </section>
  );
}

// Main App Component
function App() {
  return (
    <>
      <NavBar />
      <Section id="home" title="Home" bgColor="#f0f4ff" />
      <Section id="about" title="About" bgColor="#e0f7fa" />
      <Section id="projects" title="Projects" bgColor="#ffe0b2" />
      <Section id="contact" title="Contact" bgColor="#fce4ec" />
    </>
  );
}

// Render App
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
