import React from 'react';

function Projects() {
  const projects = [
    {
      title: "Dish Craft",
      description: "A social cooking platform where users can share recipes, create meal plans, and connect with food enthusiasts. Built with MERN stack (MongoDB, Express, React, Node.js).",
      tags: ["React", "Node.js", "MongoDB", "Redux", "JWT Auth"],
      github: "https://github.com/Dilhara2002/dish-craft",
      demo: "https://dish-craft-demo.com",
      image: "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"
    },
    {
      title: "TaskFlow",
      description: "Productivity application with Kanban-style task management, calendar integration, and team collaboration features.",
      tags: ["React", "Firebase", "Material UI", "Drag & Drop"],
      github: "https://github.com/Dilhara2002/taskflow",
      demo: "https://taskflow-demo.com",
      image: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"
    },
    {
      title: "EcoTrack",
      description: "Sustainability app that helps users track their carbon footprint and suggests eco-friendly alternatives.",
      tags: ["Next.js", "Tailwind CSS", "Chart.js", "REST API"],
      github: "https://github.com/Dilhara2002/ecotrack",
      demo: "https://ecotrack-demo.com",
      image: "https://images.unsplash.com/photo-1466611653911-95081537e5b7?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"
    },
    {
      title: "CodeCollab",
      description: "Real-time code collaboration platform with syntax highlighting and video chat functionality.",
      tags: ["Socket.io", "React", "Monaco Editor", "WebRTC"],
      github: "https://github.com/Dilhara2002/codecollab",
      demo: "https://codecollab-demo.com",
      image: "https://images.unsplash.com/photo-1555066931-4365d14bab8c?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"
    }
  ];

  return (
    <div 
      id="projects"
      style={{
        padding: '5rem 1rem',
        maxWidth: '1200px',
        margin: '0 auto',
        backgroundColor: '#f8fafc'
      }}
    >
      <div style={{ marginBottom: '3rem', textAlign: 'center' }}>
        <h2 
          style={{
            fontSize: '2.5rem',
            fontWeight: '800',
            color: '#1e293b',
            marginBottom: '1rem',
            position: 'relative',
            display: 'inline-block'
          }}
        >
          My Projects
          <span 
            style={{
              position: 'absolute',
              bottom: '-8px',
              left: '50%',
              transform: 'translateX(-50%)',
              width: '80px',
              height: '4px',
              backgroundColor: '#3b82f6',
              borderRadius: '2px'
            }}
          />
        </h2>
        <p 
          style={{
            fontSize: '1.125rem',
            color: '#64748b',
            maxWidth: '600px',
            margin: '0 auto'
          }}
        >
          Here are some of my featured projects. Each one was built to solve a specific problem or explore new technologies.
        </p>
      </div>

      <div 
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))',
          gap: '2rem',
          padding: '0 1rem'
        }}
      >
        {projects.map((project, index) => (
          <div 
            key={index}
            style={{
              backgroundColor: '#ffffff',
              borderRadius: '12px',
              overflow: 'hidden',
              boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
              border: '1px solid #e2e8f0'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'translateY(-5px)';
              e.currentTarget.style.boxShadow = '0 20px 25px -5px rgba(0, 0, 0, 0.1)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 10px 15px -3px rgba(0, 0, 0, 0.1)';
            }}
          >
            <div 
              style={{
                height: '200px',
                backgroundImage: `url(${project.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                position: 'relative'
              }}
            >
              <div 
                style={{
                  position: 'absolute',
                  top: '0',
                  left: '0',
                  right: '0',
                  bottom: '0',
                  background: 'linear-gradient(to bottom, rgba(0,0,0,0.1), rgba(0,0,0,0.7))',
                  display: 'flex',
                  alignItems: 'flex-end',
                  padding: '1rem'
                }}
              >
                <h3 
                  style={{
                    fontSize: '1.5rem',
                    fontWeight: '700',
                    color: 'white',
                    margin: '0'
                  }}
                >
                  {project.title}
                </h3>
              </div>
            </div>

            <div style={{ padding: '1.5rem' }}>
              <p 
                style={{
                  color: '#475569',
                  marginBottom: '1.5rem',
                  lineHeight: '1.6'
                }}
              >
                {project.description}
              </p>

              <div 
                style={{
                  display: 'flex',
                  flexWrap: 'wrap',
                  gap: '0.5rem',
                  marginBottom: '1.5rem'
                }}
              >
                {project.tags.map((tag, tagIndex) => (
                  <span 
                    key={tagIndex}
                    style={{
                      backgroundColor: '#e0f2fe',
                      color: '#0369a1',
                      padding: '0.25rem 0.75rem',
                      borderRadius: '9999px',
                      fontSize: '0.75rem',
                      fontWeight: '500'
                    }}
                  >
                    {tag}
                  </span>
                ))}
              </div>

              <div 
                style={{
                  display: 'flex',
                  gap: '1rem'
                }}
              >
                <a 
                  href={project.github}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    backgroundColor: '#1e293b',
                    color: 'white',
                    padding: '0.5rem 1rem',
                    borderRadius: '6px',
                    textDecoration: 'none',
                    fontSize: '0.875rem',
                    fontWeight: '600',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem',
                    transition: 'background-color 0.2s ease'
                  }}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#334155'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#1e293b'}
                >
                  <svg 
                    xmlns="http://www.w3.org/2000/svg" 
                    width="16" 
                    height="16" 
                    viewBox="0 0 24 24" 
                    fill="none" 
                    stroke="currentColor" 
                    strokeWidth="2" 
                    strokeLinecap="round" 
                    strokeLinejoin="round"
                  >
                    <path d="M9 19c-5 1.5-5-2.5-7-3m14 6v-3.87a3.37 3.37 0 0 0-.94-2.61c3.14-.35 6.44-1.54 6.44-7A5.44 5.44 0 0 0 20 4.77 5.07 5.07 0 0 0 19.91 1S18.73.65 16 2.48a13.38 13.38 0 0 0-7 0C6.27.65 5.09 1 5.09 1A5.07 5.07 0 0 0 5 4.77a5.44 5.44 0 0 0-1.5 3.78c0 5.42 3.3 6.61 6.44 7A3.37 3.37 0 0 0 9 18.13V22"></path>
                  </svg>
                  GitHub
                </a>

                <a 
                  href={project.demo}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    backgroundColor: '#3b82f6',
                    color: 'white',
                    padding: '0.5rem 1rem',
                    borderRadius: '6px',
                    textDecoration: 'none',
                    fontSize: '0.875rem',
                    fontWeight: '600',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem',
                    transition: 'background-color 0.2s ease'
                  }}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#2563eb'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#3b82f6'}
                >
                  <svg 
                    xmlns="http://www.w3.org/2000/svg" 
                    width="16" 
                    height="16" 
                    viewBox="0 0 24 24" 
                    fill="none" 
                    stroke="currentColor" 
                    strokeWidth="2" 
                    strokeLinecap="round" 
                    strokeLinejoin="round"
                  >
                    <circle cx="12" cy="12" r="10"></circle>
                    <polygon points="10 8 16 12 10 16 10 8"></polygon>
                  </svg>
                  Live Demo
                </a>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div style={{ textAlign: 'center', marginTop: '3rem' }}>
        <a 
          href="https://github.com/Dilhara2002"
          target="_blank"
          rel="noopener noreferrer"
          style={{
            backgroundColor: 'transparent',
            color: '#3b82f6',
            padding: '0.75rem 1.5rem',
            borderRadius: '6px',
            textDecoration: 'none',
            fontSize: '1rem',
            fontWeight: '600',
            border: '2px solid #3b82f6',
            display: 'inline-flex',
            alignItems: 'center',
            gap: '0.5rem',
            transition: 'all 0.2s ease'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.backgroundColor = '#f0f7ff';
            e.currentTarget.style.transform = 'translateY(-2px)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.backgroundColor = 'transparent';
